import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  // template: `<h1>Bienvenue sur {{title}}!</h1>`
  template: `<h1>Bienvenue sur {{ pokemons[1] }}!</h1>`
})
// la vue est définie dans le template du composant, la logique de la vue sera pilotée par la classe du composant qui est défini plus bas
export class AppComponent {
  // title = 'Application de pokémons';
  // décla ppt title. Va venir remplacer la valeur dans le template: `<h1>Welcome to {{title}}!</h1>` 
  pokemons = ['Bulbizarre', 'Salamèche', 'Carapuce'];
  // propriété pokemons qui contient un tab de pokemons => envoie erreur car teplate lié à la ppt title ne peut pas fonctionner
}
